/* horizontal alignment */
export type ContentJustification =
  | "flex-start"
  | "flex-end"
  | "center"
  | "space-between"
  | "space-around"
  | "space-evenly";

export type ContentDirection =
  | "row"
  | "row-reverse"
  | "column"
  | "column-reverse";

export type ContentWrap = "nowrap" | "wrap" | "wrap-reverse";

/* vertical alignment */
export type ContentAlignment =
  | "flex-start"
  | "flex-end"
  | "center"
  | "stretch"
  | "space-between"
  | "space-around";

export type ItemsAlignment =
  | "flex-start"
  | "flex-end"
  | "center"
  | "stretch"
  | "baseline";
